                <script>
                  function validateForm() {
                    var name = document.getElementById("fname").value;
                    var center = document.getElementById("center").value;
                    var message = document.getElementById("message").value;
                    var email = document.getElementById("email").value;
                    var phone = document.getElementById("mobno").value;

                    var nameError = document.getElementById("nameError");
                    var centerError = document.getElementById("centerError");
                    var messageError = document.getElementById("messageError");
                    var emailError = document.getElementById("emailError");
                    var phoneError = document.getElementById("phoneError");

                    var isValid = true;

                    // Validate name
                    if (name.trim() == '') {
                      nameError.textContent = 'Please enter your name';
                      isValid = false;
                    } else {
                      nameError.textContent = '';
                    }
                    if (name.trim() == '') {
                      centerError.textContent = 'Please select center';
                      isValid = false;
                    } else {
                      centerError.textContent = '';
                    }
                    if (name.trim() == '') {
                      messageError.textContent = 'Enter Message';
                      isValid = false;
                    } else {
                      messageError.textContent = '';
                    }

                    // Validate email
                    var emailPattern = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
                    if (!emailPattern.test(email)) {
                      emailError.textContent = 'Please enter a valid email address';
                      isValid = false;
                    } else {
                      emailError.textContent = '';
                    }

                    // Validate phone number
                    var phonePattern = /^\d{10}$/;
                    if (!phonePattern.test(phone)) {
                      phoneError.textContent = 'Please enter a 10-digit phone number';
                      isValid = false;
                    } else {
                      phoneError.textContent = '';
                    }

                    return isValid;
                  }
                </script>
